var dialog = switchBoard.startDialog(msg, 30000, 'Para mi tambien es aburrido... cambiamos de tema? '); 
 dialog.timeout = function(msg2)
  {   msg2.emote('Para mi tambien es aburrido... cambiamos de tema? ');  }
